import UIKit

class ChatLogWorker
{
    
    var repository = Repository.getInstance()
    
    func doSomeWork()
    {
    }
    
    func sendMessage(request: ChatLog.SendMessage.Request, completion: @escaping() -> Void) {
        repository.sendMessage(message: request.message, toUser: request.userToSendMessage!) {
            completion()
        }
    }
    
    func loadMessagesForTappedUser(request: ChatLog.LoadMessagesForTappedUser.Request, completion: @escaping ([Message]) -> Void) {
        guard let user = request.user else { return }
        repository.loadMessagesForUser(userToLoadMessages: user) { (messages) in
            completion(messages)
        }
    }
}
